import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';

function App() {
  const [GenreId, setGenreId] = useState('');
  const [GenreType, setGenreType] = useState('');
  const [movieGenreTypeList, setMovieGenreTypeList] = useState([]);
  const [newGenreType, setNewGenreType] = useState("");

  useEffect(() => {
    Axios.get('http://localhost:3002/api/get').then((response) => {
      setMovieGenreTypeList(response.data)
    })
  },[])

  const submitGenreType = () => { 
    Axios.post('http://localhost:3002/api/insert', {
      GenreId: GenreId,
      movieGenreType: GenreType
    });

    setMovieGenreTypeList([
      ...movieGenreTypeList,
      {
        GenreId: GenreId,
        movieGenreType: GenreType
      },
    ]);
  };

  const deleteGenreType = (GenreId) => {
    Axios.delete(`http://localhost:3002/api/delete/${GenreId}`);
  };

  const updateGenreType = (GenreId) => {
    Axios.put(`http://localhost:3002/api/update`, {
      GenreId: GenreId,
      movieGenreType: newGenreType
    });
    setNewGenreType("")
  };

  return (
    <div className="App">
      <h1> CRUD APPLICATIONS</h1>

      <div className="form">
        <label> Movie Name:</label>
        <input type="text" name="GenreId" onChange={(e) => {
          setGenreId(e.target.value)
        } }/>
        <label> GenreType:</label>
        <input type="text" name="GenreType" onChange={(e) => {
          setGenreType(e.target.value)
        }}/>

        <button onClick={submitGenreType}> Submit</button>

        {movieGenreTypeList.map((val) => {
          return (
            <div className = "card">
              <h1> GenreId: {val.GenreId} </h1>
              <p>Movie GenreType: {val.movieGenreType}</p>
              <button onClick={() => { deleteGenreType(val.GenreId) }}> Delete</button>
              <input type="text" id="updateInput" onChange={(e) => {
                setNewGenreType(e.target.value)
              } }/>
              <button onClick={() => {
                updateGenreType(val.GenreId)
              }}> Update</button>
              </div>
          );

          ;
        })}


      </div>

    </div>
  );
}

export default App;
