import React, {useState, useEffect, isValidElement} from "react";
import Axios from 'axios';
import '../App.css';

function Actors() {
  const [actorList,setActorList] = useState([]);

  const SearchActor = () => {
    Axios.get('http://localhost:3002/api/getActor').then((response) => {
        setActorList(response.data);
    })
  };

  return (
  <div className="App">
    <h1> Search Actors</h1>
    <div class = "Search Best Actors">
      <label> Find 15 Actors with Highest Rated Average Movie Ratings</label>
      <button onClick={SearchActor}>Search Actor</button>
      <div class = "actors">
        <label> 15 Top Rated Actors</label>
        {actorList.map((val) => {
          return (
            <div className = "card_lan">
              <p>Actor Name: {val.ActorName}</p>
              <p>Average Rating: {val.ave}</p>
              <br></br>
              </div>
            );
          ;
        })}
        </div>
      </div>
  </div>
  );
 }
 export default Actors;
