import React, {useState, useEffect, isValidElement} from "react";
import Axios from 'axios';
import '../App.css';

function Directors() {
  const [directorList,setDirectorList] = useState([]);

  const SearchDirector = () => {
    Axios.get('http://localhost:3002/api/getDirector').then((response) => {
      setDirectorList(response.data);
    })
  };

  return (
  <div className="App">
    <h1> Search Director</h1>
    <div class = "Search Best Actors">
      <label> Find 15 Directors with Highest Rated Average Movie Ratings</label>
      <button onClick={SearchDirector}>Search Director</button>
      <div class = "actors">
        <label> 15 Top Rated Directors</label>
        {directorList.map((val) => {
          return (
            <div className = "card_lan">
              <p>Director Name: {val.DirectorName}</p>
              <p>Average Rating: {val.ave}</p>
              <br></br>
              </div>
            );
          ;
        })}
        </div>
      </div>
  </div>
  );
 }
 export default Directors;
