import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';

function App() {
  const [GenreId, setGenreId] = useState('');
  const [GenreType, setGenreType] = useState('');
  const [GenreTypeList, setGenreTypeList] = useState([]);
  const [newGenreType, setNewGenreType] = useState("");
  const [searchGenreList, setsearchGenreList] = useState('');
  const [GenreIdList, setGenreIdList] = useState([]);

  useEffect(() => {
    Axios.get('http://localhost:3002/api/get').then((response) => {
      setGenreTypeList(response.data)
    })
  },[])



  const submitGenreType = () => { 
    Axios.post('http://localhost:3002/api/insert', {
      GenreId: GenreId,
      GenreType: GenreType
    });

    setGenreTypeList([
      ...GenreTypeList,
      {
        GenreId: GenreId,
        GenreType: GenreType
      },
    ]);
  };

  const deleteGenreType = (GenreId) => {
    Axios.delete(`http://localhost:3002/api/delete/${GenreId}`);
  };


  const search = () => { 
    Axios.post('http://localhost:3002/api/search', {
      GenreType: searchGenreList
    }).then((response) => {setGenreIdList(response.data)});
  };

  const updateGenreType = (GenreId) => {
    Axios.put(`http://localhost:3002/api/update`, {
      GenreId: GenreId,
      GenreType: newGenreType
    });
    setNewGenreType("")
  };

  return (
    <div className="App">
      <h1> Stage 4 Demo</h1>

      <div className="form">
        <label> Genre ID:</label>
        <input type="text" name="GenreId" onChange={(e) => {
          setGenreId(e.target.value)
        } }/>
        <label> Genre Type:</label>
        <input type="text" name="GenreType" onChange={(e) => {
          setGenreType(e.target.value)
        }}/>

        <button onClick={submitGenreType}> Submit</button>


        {GenreTypeList.map((val) => {
          return (
            <div className = "card">
              <h1> GenreId: {val.GenreId} </h1>
              <p>Movie GenreType: {val.GenreType}</p>
              <button onClick={() => { deleteGenreType(val.GenreId) }}> Delete</button>
              <input type="text" id="updateInput" onChange={(e) => {
                setNewGenreType(e.target.value)
              } }/>
              <button onClick={() => {
                updateGenreType(val.GenreId)
              }}> Update</button>
              </div>
          );
          ;

            })},



      </div>


      <div className="form">
        <label> Search for GenreID:</label>
        <input type="text" name="searchGenreList" onChange={(e) => {
          setsearchGenreList(e.target.value)
        }}/>

        <button onClick={search}> Search</button>

        {GenreIdList.map((val) => {
          return (
            <div className = "card">
              <h1> Genre Id: {val.GenreId} </h1>
              <p>Genre Type: {val.GenreType}</p>

              </div>
          );

          ;
        })}
      </div>



    </div>
  );
}

export default App;
