import './App.css';
import React, {useState, useEffect} from "react";
import Axios from 'axios';

function App() {
  const [GenreType, setGenreType] = useState('');
  const [GenreId, setGenreId] = useState('');
  const [GenreList, setGenreList] = useState([]);
  const [newGenreId, setNewGenreId] = useState("");

  useEffect(() => {
    Axios.get('http://localhost:3002/api/get').then((response) => {
      setGenreList(response.data)
    })
  },[])

  const submitGenreId = () => { 
    Axios.post('http://localhost:3002/api/insert', {
      GenreType: GenreType,
      GenreId: GenreId
    });
    setGenreList([
      ...GenreList,
      {
        GenreType: GenreType,
        GenreId: GenreId
      },
    ]);
  };

  const deleteGenreID = (GenreType) => {
    Axios.delete(`http://localhost:3002/api/delete/${GenreType}`);
  };

  const updateGenreID = (GenreType) => {
    Axios.put(`http://localhost:3002/api/update`, {
        GenreType: GenreType,
        GenreId: newGenreId
    });
    setNewGenreId("")
  };

  return (
    <div className="App">
      <h1> STAGE 4 DEMO</h1>

      <div className="form">
        <label> Genre Type:</label>
        <input type="text" name="GenreType" onChange={(e) => {
          setGenreType(e.target.value)
        } }/>
        <label> ID:</label>
        <input type="text" name="ID" onChange={(e) => {
          setGenreId(e.target.value)
        }}/>

        <button onClick={submitGenreId}> Submit</button>

        {GenreList.map((val) => {
          return (
            <div className = "card">
              <h1> GenreType: {val.GenreType} </h1>
              <p>Genre ID: {val.GenreId}</p>
              <button onClick={() => { deleteGenreID(val.GenreType) }}> Delete</button>
              <input type="text" id="updateInput" onChange={(e) => {
                setNewGenreId(e.target.value)
              } }/>
              <button onClick={() => {
                updateGenreID(val.GenreType)
              }}> Update</button>
              </div>
          );

          ;
        })}


      </div>

    </div>
  );
}



export default App;
